CREATE TYPE "public"."attacker" AS ENUM('ADMINISTRATORS', 'APPLICATION_USERS', 'SYSTEM_USERS', 'UNAUTHORISED_PARTIES');
CREATE TYPE "public"."confidentiality_level" AS ENUM('PUBLIC', 'INTERNAL', 'CONFIDENTIAL', 'HIGHLY_CONFIDENTIAL');
CREATE TYPE "public"."point_of_attack" AS ENUM('COMMUNICATION_INFRASTRUCTURE', 'COMMUNICATION_INTERFACES', 'DATA_STORAGE_INFRASTRUCTURE', 'PROCESSING_INFRASTRUCTURE', 'USER_BEHAVIOUR', 'USER_INTERFACE');
CREATE TABLE "assets" (
	"id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY (sequence name "assets_id_seq" INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START WITH 1 CACHE 1),
	"name" varchar(255) NOT NULL,
	"description" text NOT NULL,
	"confidentiality" integer NOT NULL,
	"integrity" integer NOT NULL,
	"availability" integer NOT NULL,
	"confidentialityJustification" text NOT NULL,
	"integrityJustification" text NOT NULL,
	"availabilityJustification" text NOT NULL,
	"createdAt" timestamp with time zone DEFAULT now() NOT NULL,
	"updatedAt" timestamp with time zone DEFAULT now() NOT NULL,
	"projectId" integer NOT NULL,
	CONSTRAINT "assets_name_not_empty" CHECK ("assets"."name" <> ''),
	CONSTRAINT "assets_confidentiality_min_max" CHECK ("assets"."confidentiality" between 1 and 5),
	CONSTRAINT "assets_integrity_min_max" CHECK ("assets"."integrity" between 1 and 5),
	CONSTRAINT "assets_availability_min_max" CHECK ("assets"."availability" between 1 and 5)
);

CREATE TABLE "catalog_measures" (
	"id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY (sequence name "catalog_measures_id_seq" INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START WITH 1 CACHE 1),
	"name" varchar(255) NOT NULL,
	"description" text,
	"pointOfAttack" "point_of_attack" NOT NULL,
	"attacker" "attacker" NOT NULL,
	"probability" integer,
	"confidentiality" boolean NOT NULL,
	"integrity" boolean NOT NULL,
	"availability" boolean NOT NULL,
	"createdAt" timestamp with time zone DEFAULT now() NOT NULL,
	"updatedAt" timestamp with time zone DEFAULT now() NOT NULL,
	"catalogId" integer NOT NULL,
	CONSTRAINT "catalog_measures_probability_min_max" CHECK ("catalog_measures"."probability" between 1 and 5)
);

CREATE TABLE "catalog_threats" (
	"id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY (sequence name "catalog_threats_id_seq" INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START WITH 1 CACHE 1),
	"name" varchar(255) NOT NULL,
	"description" text,
	"pointOfAttack" "point_of_attack" NOT NULL,
	"attacker" "attacker" NOT NULL,
	"probability" integer NOT NULL,
	"confidentiality" boolean NOT NULL,
	"integrity" boolean NOT NULL,
	"availability" boolean NOT NULL,
	"isDefault" boolean DEFAULT true,
	"createdAt" timestamp with time zone DEFAULT now() NOT NULL,
	"updatedAt" timestamp with time zone DEFAULT now() NOT NULL,
	"catalogId" integer NOT NULL,
	CONSTRAINT "catalog_threats_probability_min_max" CHECK ("catalog_threats"."probability" between 1 and 5)
);

CREATE TABLE "catalogs" (
	"id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY (sequence name "catalogs_id_seq" INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START WITH 1 CACHE 1),
	"name" varchar(255) NOT NULL,
	"language" varchar(255) NOT NULL,
	"createdAt" timestamp with time zone DEFAULT now() NOT NULL,
	"updatedAt" timestamp with time zone DEFAULT now() NOT NULL,
	CONSTRAINT "catalogs_name_not_empty" CHECK ("catalogs"."name" <> '')
);

CREATE TABLE "component_types" (
	"id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY (sequence name "component_types_id_seq" INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START WITH 1 CACHE 1),
	"name" varchar(255) NOT NULL,
	"pointsOfAttack" "point_of_attack"[] NOT NULL,
	"symbol" text,
	"createdAt" timestamp with time zone DEFAULT now() NOT NULL,
	"updatedAt" timestamp with time zone DEFAULT now() NOT NULL,
	"projectId" integer
);

CREATE TABLE "measure_impacts" (
	"id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY (sequence name "measure_impacts_id_seq" INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START WITH 1 CACHE 1),
	"description" text,
	"setsOutOfScope" boolean NOT NULL,
	"impactsProbability" boolean NOT NULL,
	"impactsDamage" boolean NOT NULL,
	"probability" integer,
	"damage" integer,
	"createdAt" timestamp with time zone DEFAULT now() NOT NULL,
	"updatedAt" timestamp with time zone DEFAULT now() NOT NULL,
	"threatId" integer NOT NULL,
	"measureId" integer NOT NULL,
	CONSTRAINT "measure_impacts_measure_id_threat_id_unique" UNIQUE("measureId","threatId"),
	CONSTRAINT "measure_impacts_probability_min_max" CHECK ("measure_impacts"."probability" between 1 and 5),
	CONSTRAINT "measure_impacts_probability" CHECK ("measure_impacts"."impactsProbability" = false OR "measure_impacts"."probability" IS NOT NULL),
	CONSTRAINT "measure_impacts_damage_min_max" CHECK ("measure_impacts"."damage" between 1 and 5),
	CONSTRAINT "measure_impacts_damage" CHECK ("measure_impacts"."impactsDamage" = false OR "measure_impacts"."damage" IS NOT NULL)
);

CREATE TABLE "measures" (
	"id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY (sequence name "measures_id_seq" INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START WITH 1 CACHE 1),
	"name" varchar(255) NOT NULL,
	"description" text,
	"scheduledAt" timestamp with time zone NOT NULL,
	"catalogMeasureId" integer,
	"createdAt" timestamp with time zone DEFAULT now() NOT NULL,
	"updatedAt" timestamp with time zone DEFAULT now() NOT NULL,
	"projectId" integer NOT NULL,
	CONSTRAINT "measures_name_not_empty" CHECK ("measures"."name" <> '')
);

CREATE TABLE "projects" (
	"id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY (sequence name "projects_id_seq" INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START WITH 1 CACHE 1),
	"name" varchar(255) NOT NULL,
	"description" text,
	"confidentialityLevel" "confidentiality_level" DEFAULT 'INTERNAL' NOT NULL,
	"lineOfToleranceGreen" integer DEFAULT 6,
	"lineOfToleranceRed" integer DEFAULT 15,
	"createdAt" timestamp with time zone DEFAULT now() NOT NULL,
	"updatedAt" timestamp with time zone DEFAULT now() NOT NULL,
	"catalogId" integer NOT NULL,
	CONSTRAINT "projects_name_not_empty" CHECK ("projects"."name" <> '')
);

CREATE TABLE "systems" (
	"id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY (sequence name "systems_id_seq" INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START WITH 1 CACHE 1),
	"data" jsonb,
	"image" text,
	"createdAt" timestamp with time zone DEFAULT now() NOT NULL,
	"updatedAt" timestamp with time zone DEFAULT now() NOT NULL,
	"projectId" integer NOT NULL,
	CONSTRAINT "project_id" UNIQUE("projectId")
);

CREATE TABLE "threats" (
	"id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY (sequence name "threats_id_seq" INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START WITH 1 CACHE 1),
	"pointOfAttackId" varchar(21) NOT NULL,
	"name" varchar(255) NOT NULL,
	"description" text,
	"pointOfAttack" "point_of_attack" NOT NULL,
	"attacker" "attacker" NOT NULL,
	"probability" integer NOT NULL,
	"confidentiality" boolean NOT NULL,
	"integrity" boolean NOT NULL,
	"availability" boolean NOT NULL,
	"doneEditing" boolean DEFAULT false NOT NULL,
	"createdAt" timestamp with time zone DEFAULT now() NOT NULL,
	"updatedAt" timestamp with time zone DEFAULT now() NOT NULL,
	"catalogThreatId" integer NOT NULL,
	"projectId" integer NOT NULL,
	CONSTRAINT "threats_name_not_empty" CHECK ("threats"."name" <> ''),
	CONSTRAINT "threats_probability_min_max" CHECK ("threats"."probability" between 1 and 5)
);

CREATE TABLE "tokens" (
	"id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY (sequence name "tokens_id_seq" INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START WITH 1 CACHE 1),
	"token" varchar(500) NOT NULL,
	"expiresAt" integer NOT NULL,
	"createdAt" timestamp with time zone DEFAULT now() NOT NULL
);

CREATE TABLE "users" (
	"id" integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY (sequence name "users_id_seq" INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START WITH 1 CACHE 1),
	"firstname" varchar(255) NOT NULL,
	"lastname" varchar(255) NOT NULL,
	"email" varchar(255) NOT NULL,
	"createdAt" timestamp with time zone DEFAULT now() NOT NULL,
	"updatedAt" timestamp with time zone DEFAULT now() NOT NULL
);

CREATE TABLE "users_catalogs" (
	"role" smallint NOT NULL,
	"createdAt" timestamp with time zone DEFAULT now() NOT NULL,
	"updatedAt" timestamp with time zone DEFAULT now() NOT NULL,
	"userId" integer NOT NULL,
	"catalogId" integer NOT NULL
);

CREATE TABLE "users_projects" (
	"role" smallint NOT NULL,
	"createdAt" timestamp with time zone DEFAULT now() NOT NULL,
	"updatedAt" timestamp with time zone DEFAULT now() NOT NULL,
	"userId" integer NOT NULL,
	"projectId" integer NOT NULL
);

ALTER TABLE "assets" ADD CONSTRAINT "assets_projectId_projects_id_fk" FOREIGN KEY ("projectId") REFERENCES "public"."projects"("id") ON DELETE cascade ON UPDATE cascade;
