ALTER TABLE "catalog_threats" ADD CONSTRAINT "catalog_threats_catalogId_catalogs_id_fk" FOREIGN KEY ("catalogId") REFERENCES "public"."catalogs"("id") ON DELETE cascade ON UPDATE cascade;
ALTER TABLE "component_types" ADD CONSTRAINT "component_types_projectId_projects_id_fk" FOREIGN KEY ("projectId") REFERENCES "public"."projects"("id") ON DELETE cascade ON UPDATE cascade;
ALTER TABLE "measure_impacts" ADD CONSTRAINT "measure_impacts_threatId_threats_id_fk" FOREIGN KEY ("threatId") REFERENCES "public"."threats"("id") ON DELETE cascade ON UPDATE cascade;
ALTER TABLE "measure_impacts" ADD CONSTRAINT "measure_impacts_measureId_measures_id_fk" FOREIGN KEY ("measureId") REFERENCES "public"."measures"("id") ON DELETE cascade ON UPDATE cascade;
ALTER TABLE "measures" ADD CONSTRAINT "measures_catalogMeasureId_catalog_measures_id_fk" FOREIGN KEY ("catalogMeasureId") REFERENCES "public"."catalog_measures"("id") ON DELETE set null ON UPDATE cascade;
ALTER TABLE "measures" ADD CONSTRAINT "measures_projectId_projects_id_fk" FOREIGN KEY ("projectId") REFERENCES "public"."projects"("id") ON DELETE cascade ON UPDATE cascade;
ALTER TABLE "projects" ADD CONSTRAINT "projects_catalogId_catalogs_id_fk" FOREIGN KEY ("catalogId") REFERENCES "public"."catalogs"("id") ON DELETE no action ON UPDATE cascade;
ALTER TABLE "systems" ADD CONSTRAINT "systems_projectId_projects_id_fk" FOREIGN KEY ("projectId") REFERENCES "public"."projects"("id") ON DELETE cascade ON UPDATE cascade;
ALTER TABLE "threats" ADD CONSTRAINT "threats_catalogThreatId_catalog_threats_id_fk" FOREIGN KEY ("catalogThreatId") REFERENCES "public"."catalog_threats"("id") ON DELETE cascade ON UPDATE cascade;
ALTER TABLE "threats" ADD CONSTRAINT "threats_projectId_projects_id_fk" FOREIGN KEY ("projectId") REFERENCES "public"."projects"("id") ON DELETE cascade ON UPDATE cascade;
ALTER TABLE "users_catalogs" ADD CONSTRAINT "users_catalogs_userId_users_id_fk" FOREIGN KEY ("userId") REFERENCES "public"."users"("id") ON DELETE cascade ON UPDATE cascade;
ALTER TABLE "users_catalogs" ADD CONSTRAINT "users_catalogs_catalogId_catalogs_id_fk" FOREIGN KEY ("catalogId") REFERENCES "public"."catalogs"("id") ON DELETE cascade ON UPDATE cascade;
ALTER TABLE "users_projects" ADD CONSTRAINT "users_projects_userId_users_id_fk" FOREIGN KEY ("userId") REFERENCES "public"."users"("id") ON DELETE cascade ON UPDATE cascade;
ALTER TABLE "users_projects" ADD CONSTRAINT "users_projects_projectId_projects_id_fk" FOREIGN KEY ("projectId") REFERENCES "public"."projects"("id") ON DELETE cascade ON UPDATE cascade;
