// Deep-cloning VALID_SYSTEM to avoid mutation issues
const VALID_UPDATE_SYSTEM: { data: SystemData; image: string } = JSON.parse(JSON.stringify(VALID_SYSTEM));
const newComponent: Component = {
    id: nanoid(),
    name: "Client 123",
    type: ComponentType.client,
    x: 410,
    y: 95,
    gridX: 82,
    gridY: 19,
    width: 80,
    height: 80,
    selected: false,
    projectId: 5,
    symbol: "data:image/png;base64,",
};
VALID_UPDATE_SYSTEM.data.components.push(newComponent);
VALID_UPDATE_SYSTEM.image =
    "data:image/png;base64,";

let projectId: number;
let catalogId: number;
let cookies: string[];
let csrfToken: string;

beforeAll(async () => {
    // Get CSRF token
    const csrfRes = await request(app).get("/api/csrf-token"); // Replace with your actual path
    csrfToken = csrfRes.body.token;

    const setCookieHeader = csrfRes.headers["set-cookie"];
    cookies = setCookieHeader !== undefined ? [setCookieHeader] : [];
    cookies = [...cookies, "accessToken=fakeaccessToken"];
    expect(csrfRes.status).toBe(200);
    csrfToken = csrfRes.body.token;
});

beforeEach(async () => {
    const catalog = (
        await db
            .insert(catalogs)
            .values({
                name: "TEST Katalog",
                language: LANGUAGES.DE,
            })
            .returning()
    ).at(0);
    catalogId = catalog!.id;

    const authRes = await request(app).get("/api/auth/status").set("X-CSRF-TOKEN", csrfToken).set("Cookie", cookies);
    const userId = authRes.body.data.userId;

    await db.insert(usersCatalogs).values({
        userId,
        catalogId,
        role: USER_ROLES.OWNER,
    });

    const res = await request(app)
        .post("/api/projects")
        .send({ ...VALID_PROJECT, catalogId })
        .set("X-CSRF-TOKEN", csrfToken)
        .set("Cookie", cookies);
    projectId = res.body.id;
});

it("should be a empty system", async () => {
    const res = await request(app)
        .get(`/api/projects/${projectId}/system`)
        .set("X-CSRF-TOKEN", csrfToken)
        .set("Cookie", cookies);
    expect(res.statusCode).toEqual(200);
    expect(res.body.data).toBe(null);
    expect(res.body.image).toBe(null);
});

it("should save a valid system", async () => {
    const res = await request(app)
        .put(`/api/projects/${projectId}/system`)
        .send(VALID_SYSTEM)
        .set("X-CSRF-TOKEN", csrfToken)
        .set("Cookie", cookies);
    expect(res.statusCode).toEqual(200);
    expect(res.body.image).toBe(VALID_SYSTEM.image);
    expect(res.body.data.components).toEqual(VALID_SYSTEM.data.components);
    expect(res.body.data.connections).toEqual(VALID_SYSTEM.data.connections);
    expect(res.body.data.connectionPoints).toEqual(VALID_SYSTEM.data.connectionPoints);
    expect(res.body.data.connectionPoints).toEqual(VALID_SYSTEM.data.connectionPoints);
});

it("should update a system", async () => {
    const res = await request(app)
        .put(`/api/projects/${projectId}/system`)
        .send(VALID_UPDATE_SYSTEM)
        .set("X-CSRF-TOKEN", csrfToken)
        .set("Cookie", cookies);
    expect(res.statusCode).toEqual(200);
    expect(res.body.image).toBe(VALID_UPDATE_SYSTEM.image);
    expect(res.body.data.components).toEqual(VALID_UPDATE_SYSTEM.data.components);
    expect(res.body.data.connections).toEqual(VALID_UPDATE_SYSTEM.data.connections);
    expect(res.body.data.connectionPoints).toEqual(VALID_UPDATE_SYSTEM.data.connectionPoints);
    expect(res.body.data.connectionPoints).toEqual(VALID_UPDATE_SYSTEM.data.connectionPoints);
});
